mapper.sql:

			t_user.id = #{query.userId}


			and t_user.phone like concat('%', #{query.userPhone}, '%')


			and t_user.nickname like concat('%', #{query.userNickname}, '%')


			and t_user.headicon like concat('%', #{query.userHeadicon}, '%')


			and t_order.id = #{query.orderId}


			and t_order.order_no like concat('%', #{query.orderOrderNo}, '%')


			and t_order.total = #{query.orderTotal}


			and t_order.type = #{query.orderType}


			and t_order.sell_id = #{query.orderSellId}


			and t_order.buy_id = #{query.orderBuyId}


			and t_order.create_time  >=  #{query.orderCreateTimeStart}


			and t_order.create_time  <=  #{query.orderCreateTimeEnd}


			and t_order.create_time  >=  #{query.orderCreateTimeStart} and t_order.create_time  <=  #{query.orderCreateTimeEnd}


			and t_order.update_time  >=  #{query.orderUpdateTimeStart}


			and t_order.update_time  <=  #{query.orderUpdateTimeEnd}


			and t_order.update_time  >=  #{query.orderUpdateTimeStart} and t_order.update_time  <=  #{query.orderUpdateTimeEnd}


			and t_order.status = #{query.orderStatus}


			and t_order.sell_confirm_time  >=  #{query.orderSellConfirmTimeStart}


			and t_order.sell_confirm_time  <=  #{query.orderSellConfirmTimeEnd}


			and t_order.sell_confirm_time  >=  #{query.orderSellConfirmTimeStart} and t_order.sell_confirm_time  <=  #{query.orderSellConfirmTimeEnd}


			and t_order.buy_confirm_time  >=  #{query.orderBuyConfirmTimeStart}


			and t_order.buy_confirm_time  <=  #{query.orderBuyConfirmTimeEnd}


			and t_order.buy_confirm_time  >=  #{query.orderBuyConfirmTimeStart} and t_order.buy_confirm_time  <=  #{query.orderBuyConfirmTimeEnd}


			and t_order.deal_time  >=  #{query.orderDealTimeStart}


			and t_order.deal_time  <=  #{query.orderDealTimeEnd}


			and t_order.deal_time  >=  #{query.orderDealTimeStart} and t_order.deal_time  <=  #{query.orderDealTimeEnd}


        and
        t_user.id = t_order.sell_id

